package com.illumi.dms.common.utils;import com.illumi.dms.common.Consts;import java.util.Map;public class SqlUtils {    public static String concatSql (Map<String,Object> map) {        StringBuilder sb = new StringBuilder();        String fieldSql;        for (String fieldName : map.keySet()) {            if (map.get(fieldName)!=null){                fieldSql = getFieldSql(fieldName, map.get(fieldName));                sb.append(fieldSql);            }        }        return sb.toString();    }    public static String getFieldSql(String fieldName,Object fieldValue){        String result = "";        switch (fieldName)        {            case Consts.START_TIME: result = " and date >= '" + fieldValue +"'";break;            case Consts.END_TIME: result = " and date <= '" + fieldValue +"'";break;            case Consts.IS_ABROAD: result = " and "+fieldName+"="+fieldValue;break;            case Consts.PERIOD: result = " and "+fieldName+"="+fieldValue;break;        }        return result;    }}