package com.illumi.dms.controller;import com.illumi.dms.common.Consts;import com.illumi.dms.model.ActiveUser;import com.jayqqaa12.jbase.jfinal.ext.ctrl.EasyuiController;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.route.ControllerBind;import org.apache.log4j.Logger;@ControllerBind(controllerKey = "/statistic/user",viewPath="/page/system")public class UserStatisticsController  extends EasyuiController {    private static final  Logger logger = Logger.getLogger(UserStatisticsController.class);    public void  list(){        String startTime= getPara(Consts.START_TIME);        String endTime= getPara(Consts.END_TIME);        getPara(Consts.IS_ABROAD);        String where =" and date is not null";        DataGrid<ActiveUser> dg = new DataGrid();        dg.sortName = this.getPara("sort", "date");        dg.sortOrder = this.getPara("order", "desc");        dg.page = this.getParaToInt("page", 1);        dg.total = this.getParaToInt("rows", 20);        renderJson(ActiveUser.dao.listByDataGridBySortSql(dg,getFrom(ActiveUser.dao.tableName),where));    }}