package com.illumi.dms.controller;import com.illumi.dms.model.ActiveUser;import com.jayqqaa12.jbase.jfinal.ext.ctrl.EasyuiController;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.route.ControllerBind;@ControllerBind(controllerKey = "/statistic/user",viewPath="/page/system")public class UserStatisticsController  extends EasyuiController {    public void  list(){        String where =" and date is not null";        DataGrid<ActiveUser> dg = new DataGrid();        dg.sortName = this.getPara("sort", "date");        dg.sortOrder = this.getPara("order", "desc");        dg.page = this.getParaToInt("page", 1);        dg.total = this.getParaToInt("rows", 20);        renderJson(ActiveUser.dao.listByDataGridBySortSql(dg,getFrom(ActiveUser.dao.tableName),where));    }}