package com.illumi.dms.model.terminal;import com.jayqqaa12.jbase.jfinal.ext.model.EasyuiModel;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.plugin.tablebind.TableBind;import java.util.List;@TableBind(tableName = "dms_network_operator_view")public class NetOperator extends EasyuiModel<NetOperator> {    private static final long serialVersionUID = -7615377924993713398L;    public static NetOperator dao = new NetOperator();    public DataGrid<NetOperator> myFind(String sql, String limit) {        List<NetOperator> NetWorkStats = NetOperator.dao.find(sql);        if (sql.contains("limit")) {            sql = sql.split("limit")[0];        }        if (sql.contains("order by")) {            sql = sql.split("order by")[0];        }        long count = this.findFirst(" select count(*) as c from ( " + sql + ") as a").getLong("c");        DataGrid<NetOperator> dg = new DataGrid<>();        dg.rows = NetWorkStats;        dg.total = (int) count;        return dg;    }}