package com.illumi.dms.model.terminal;import com.jayqqaa12.jbase.jfinal.ext.model.EasyuiModel;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.plugin.tablebind.TableBind;import java.util.List;@TableBind(tableName = "dms_net_status_view")public class NetWorkStat  extends EasyuiModel<NetWorkStat> {    private static final long serialVersionUID = -7615377924993713398L;    public static NetWorkStat dao = new NetWorkStat();    public DataGrid<NetWorkStat> myFind(String sql, String limit) {        List<NetWorkStat> NetWorkStats = NetWorkStat.dao.find(sql);        if (sql.contains("limit")) {            sql = sql.split("limit")[0];        }        if (sql.contains("order by")) {            sql = sql.split("order by")[0];        }        long count = this.findFirst(" select count(*) as c from ( " + sql + ") as a").getLong("c");        DataGrid<NetWorkStat> dg = new DataGrid<>();        dg.rows = NetWorkStats;        dg.total = (int) count;        return dg;    }}