package com.illumi.dms.model.test_dms;import com.illumi.dms.common.utils.ValidateObjectUtil;import com.illumi.dms.model.test_poker.TUserBaseIinfo;import com.jayqqaa12.jbase.jfinal.ext.model.EasyuiModel;import com.jayqqaa12.model.easyui.DataGrid;import com.jayqqaa12.model.easyui.Form;import com.jfinal.ext.plugin.tablebind.TableBind;import com.jfinal.kit.JsonKit;import org.slf4j.LoggerFactory;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.List;@TableBind(tableName = "dms_user_view")public class DmsUserView extends EasyuiModel<DmsUserView> {    private static org.slf4j.Logger logger = LoggerFactory.getLogger(DmsUserView.class);    private static  SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");    public static DmsUserView dao = new DmsUserView();    public DataGrid<DmsUserView> listByDataGridBySortSql(DataGrid<DmsUserView> dg, Form f, String sortStr) {        DataGrid<DmsUserView> dmsUserViewDataGrid = super.listByDataGridBySortSql(dg, f, sortStr);        return dmsUserViewDataGrid;    }    public List<DmsUserView> getYesdayRegisterCount(){        Calendar calendar = Calendar.getInstance();        calendar.add(Calendar.DAY_OF_MONTH,-1);        String sql  = String.format("select * from dms_user_view  where date = '%s'   order by date,is_abroad desc limit 2",dateFormat.format(calendar.getTime()));        logger.info(String.format(">>>>>>>>>>class:{%s},method:{%s},sql:{%s}",DmsUserView.class.getName(),Thread.currentThread().getStackTrace()[1].getMethodName(),sql));        List<DmsUserView> dmsUserViewList = this.find(sql);        logger.info(String.format(">>>>>>>>>>List<DmsUserView> dmsUserViewList:%s", JsonKit.toJson(dmsUserViewList,8)));        return  dmsUserViewList;    }    public static DmsUserView getTodayView(String type) {        return TUserBaseIinfo.dao.todayCollectData(type);    }}