package com.illumi.dms.model.user;import com.jayqqaa12.jbase.jfinal.ext.model.EasyuiModel;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.plugin.tablebind.TableBind;import java.util.List;@TableBind(tableName = "dms_app_version_person")public class AppPerson  extends EasyuiModel<AppPerson> {    private static final long serialVersionUID = -7615377924993713398L;    public static AppPerson dao = new AppPerson();    public DataGrid<AppPerson> myFind(String sql, String limit) {        List<AppPerson> rows = AppPerson.dao.find(sql);        if (sql.contains("limit")) {            sql = sql.split("limit")[0];        }        if (sql.contains("order by")) {            sql = sql.split("order by")[0];        }        long count = this.findFirst(" select count(*) as c from ( " + sql + ") as a").getLong("c");        DataGrid<AppPerson> dg = new DataGrid<>();        dg.rows = rows;        dg.total = (int) count;        return dg;    }}