package com.illumi.dms.model.user;import com.jayqqaa12.jbase.jfinal.ext.model.EasyuiModel;import com.jayqqaa12.model.easyui.DataGrid;import com.jfinal.ext.plugin.tablebind.TableBind;import java.util.List;@TableBind(tableName = "dms_app_version_view")public class AppVersion extends EasyuiModel<AppVersion> {    private static final long serialVersionUID = -7615377924993713398L;    public static AppVersion dao = new AppVersion();    public DataGrid<AppVersion> myFind(String sql, String limit){        List<AppVersion> appVersions = AppVersion.dao.find(sql);        if (sql.contains("limit")){            sql = sql.split("limit")[0];        }        if (sql.contains("order by")){            sql = sql.split("order by")[0];        }        long count = this.findFirst(" select count(*) as c from ( " +sql+") as a").getLong("c");        DataGrid<AppVersion> dg = new DataGrid<>();        dg.rows = appVersions;        dg.total = (int)count;        return dg;    }}