package com.illumi.dms.validator;import com.jayqqaa12.jbase.jfinal.ext.Validator;import com.jfinal.core.Controller;import java.text.DateFormat;import java.text.ParseException;import java.text.SimpleDateFormat;import static com.illumi.dms.common.Consts.END_TIME;import static com.illumi.dms.common.Consts.START_TIME;public class UserStatisticsValidator extends Validator {    @Override    protected void validate(Controller c) {        super.validate(c);        DateFormat format = new SimpleDateFormat("yyyy-MM-dd");        try {            if(format.parse(c.getPara(START_TIME)).getTime()>format.parse(c.getPara(END_TIME)).getTime()){                addError("开始时间大于结束时间！！！");            }        } catch (ParseException e) {            addError("非法的时间格式！！！");        }    }}